function create_worker_input(e){var t=$('<div class="worker input-group form-group"></div>'),r=$('<input class="worker-input form-control" type="text" name="workers[]" value="'+e+'"/>'),n=$('<span class="input-group-btn"><button class="btn btn-danger worker-remove"  data-toggle="tooltip" data-placement="right" title="Remove worker"><span class="glyphicon glyphicon-trash" aria-hidden="true"></span></button></span>');n.click(function(){$(this).parent().remove()}),t.append(r),t.append(n),$("#workers").append(t)}function add_worker(){$input=$("#new-worker-input"),$input.val().length>0?(create_worker_input($input.val()),$input.val("")):alert("Field is empty, please enter a worker name first")}function initialize_new_worker_form(){$new_worker_button=$("#new-worker-add"),$new_worker_button.click(add_worker),$("#new-worker-input").keypress(function(e){if(13==e.which)return $new_worker_button.click(),!1})}$(document).ready(function(){initialize_new_worker_form(),$('[data-toggle="tooltip"]').tooltip()});